function api1Service () {

    var successResponse = function (req, res) {
        res.sendStatus(200);
    };

    // Multiple middleware test
    var middleware1 = function (req, res, next) {
        res.message = 'Hello';
        next();
    };
    var middleware2 = function (req, res, next) {
        res.message += ' World';
        next();
    };
    var middleware3 = function (req, res) {
        res.message += '!';
        res.send(res.message);
    };

    var routes = [
        {path: '/:param/_api/hello', httpMethod: 'GET', middleware: [middleware1, middleware2, middleware3]},
        {path: '/:param/_api/settings', httpMethod: 'POST', middleware: [successResponse]},

        // duplicate routes inside the same package should not trigger any errors
        {path: '/open', httpMethod: 'POST', middleware: [successResponse]},
        {path: '/open', httpMethod: 'POST', middleware: [successResponse]}
    ];

    return {
        Routes: routes
    }
}

module.exports = api1Service;